/*Given two strings s and t, return true if s is a subsequence of t, or false otherwise.

        A subsequence of a string is a new string that is formed from the original string by deleting some (can be none)
        of the characters without disturbing the relative positions of the remaining characters.
        (i.e., "ace" is a subsequence of "abcde" while "aec" is not).



        Example 1:

        Input: s = "abc", t = "ahbgdc"
        Output: true

        Example 2:

        Input: s = "axc", t = "ahbgdc"
        Output: false


        Constraints:

        0 <= s.length <= 100
        0 <= t.length <= 104
        s and t consist only of lowercase English letters.*/
import java.lang.StringBuilder;
import java.lang.StringBuffer;
import java.util.*;
public class IsSubsequence {


    public static void main(String[] args) {

        //   isSubsequence("abc", "ahbgdc"); //expected true
        //  isSubsequence("axc", "ahbgdc"); //expected false
        // isSubsequence("acb", "ahbgdc"); //expected false
        //isSubsequence("bb", "ahbgdc"); //expected false
        //isSubsequence("bab", "ahbgdc"); //expected false
        //isSubsequence("b", "abc");
        isSubsequence("leeeeetcode", "");

    }

    public static boolean isSubsequence(String s, String t) {
        if (s.length() == 0) {
            return true;
        }
        if (t.length() == 0) {
            return false;
        }

        int sIndex = 0;

        StringBuffer sbf = new StringBuffer();
        for (int i = 0; i < t.length(); i++) {
            if (t.charAt(i) == s.charAt(sIndex)) {
                sIndex++;
                if (sIndex == s.length()) {
                    return true;
            }
        }

        }
        return false;
    }
}





